import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function ReviewList() {
  const [reviews, setReviews] = useState([]);
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    axios.get('http://localhost:5000/reviews')
      .then(res => {
        console.log('Reviews data:', res.data);
        setReviews(res.data);
      })
      .catch(err => {
        console.error('Error fetching reviews:', err);
      });
  }, []);

  const handleEdit = (id) => {
    // Navigate to the edit page with the review ID
    navigate(`/edit`);
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/reviews/${id}`)
      .then(res => {
        console.log('Review deleted:', res.data);
        setReviews(reviews.filter(review => review._id !== id));
      })
      .catch(err => {
        console.error('Error deleting review:', err);
      });
  };

  return (
    <div className="review-list-container">
      <h2 className="title">Book Reviews</h2>
      <table className="reviews-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Rating</th>
            <th>Review</th>
            <th>Action</th> {/* New Action column */}
          </tr>
        </thead>
        <tbody>
          {reviews.map((review) => (
            <tr key={review._id}>
              <td>{review.title}</td>
              <td>{review.author}</td>
              <td>{review.rating} / 5</td>
              <td>{review.reviewText}</td>
              <td>
                <button onClick={() => handleEdit(review._id)}>Edit</button> {/* Edit button */}
                <button onClick={() => handleDelete(review._id)}>Delete</button> {/* Delete button */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ReviewList;

// Inject styles using a <style> tag
const style = document.createElement('style');
style.innerHTML = `
  .review-list-container {
    padding: 20px;
    font-family: Arial, sans-serif;
  }
  .title {
    text-align: center;
    font-size: 2em;
    margin-bottom: 20px;
  }
  .reviews-table {
    width: 100%;
    border-collapse: collapse;
    margin: 0 auto;
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }
  .reviews-table th {
    background-color: #f4f4f4;
    text-align: left;
    padding: 12px 15px;
    font-size: 1.1em;
    color: #333;
  }
  .reviews-table td {
    background-color: #fff;
    border-bottom: 1px solid #ddd;
    text-align: left;
    padding: 12px 15px;
    font-size: 1em;
    color: #555;
  }
  .reviews-table tr:hover {
    background-color: #f9f9f9;
  }
  .reviews-table button {
    margin: 0 5px;
    padding: 5px 10px;
    cursor: pointer;
    border: none;
    border-radius: 4px;
    background-color: #007bff;
    color: white;
  }
  .reviews-table button:hover {
    background-color: #0056b3;
  }
  .reviews-table button:active {
    background-color: #004085;
  }
`;
document.head.appendChild(style);
