import React, { useState } from 'react';
import axios from 'axios';

function AddReview() {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    rating: 1,
    reviewText: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/reviews', formData)
      .then(() => alert('Review Added'))
      .catch(err => console.error(err));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Title" onChange={e => setFormData({ ...formData, title: e.target.value })} />
      <input type="text" placeholder="Author" onChange={e => setFormData({ ...formData, author: e.target.value })} />
      <input type="number" placeholder="Rating" min="1" max="5" onChange={e => setFormData({ ...formData, rating: e.target.value })} />
      <textarea placeholder="Review" onChange={e => setFormData({ ...formData, reviewText: e.target.value })}></textarea>
      <button type="submit">Add Review</button>
    </form>
  );
}

export default AddReview;
