import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditReview.css';  // Make sure to import the CSS file

function EditReview() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    rating: 1,
    reviewText: '',
  });

  useEffect(() => {
    axios.get(`http://localhost:5000/reviews/${id}`)
      .then(res => setFormData(res.data))
      .catch(err => console.error(err));
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/reviews/${id}`, formData)
      .then(() => {
        alert('Review Updated');
        navigate('/');
      })
      .catch(err => console.error(err));
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Edit Your Review</h2>
      <form onSubmit={handleSubmit} className="review-form">
        <input 
          type="text" 
          value={formData.title} 
          onChange={e => setFormData({ ...formData, title: e.target.value })} 
          placeholder="Title" 
          className="form-input" 
        />
        <input 
          type="text" 
          value={formData.author} 
          onChange={e => setFormData({ ...formData, author: e.target.value })} 
          placeholder="Author" 
          className="form-input" 
        />
        <input 
          type="number" 
          value={formData.rating} 
          min="1" 
          max="5" 
          onChange={e => setFormData({ ...formData, rating: e.target.value })} 
          placeholder="Rating (1-5)" 
          className="form-input" 
        />
        <textarea 
          value={formData.reviewText} 
          onChange={e => setFormData({ ...formData, reviewText: e.target.value })} 
          placeholder="Review" 
          className="form-textarea" 
        />
        <button type="submit" className="form-button">Update Review</button>
      </form>
    </div>
  );
}

export default EditReview;
