import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function ReviewList() {
  const [reviews, setReviews] = useState([]);
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    axios.get('http://localhost:5000/reviews')
      .then(res => {
        console.log('Reviews data:', res.data);
        setReviews(res.data);
      })
      .catch(err => {
        console.error('Error fetching reviews:', err);
      });
  }, []);

  const handleEdit = (id) => {
    // Navigate to the edit page with the review ID
    navigate(`/edit/${id}`);
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/reviews/${id}`)
      .then(res => {
        console.log('Review deleted:', res.data);
        setReviews(reviews.filter(review => review._id !== id));
      })
      .catch(err => {
        console.error('Error deleting review:', err);
      });
  };

  return (
    <div className="review-list-container">
      <h2 className="title">Book Reviews</h2>
      <table className="reviews-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Rating</th>
            <th>Review</th>
            <th>Action</th> {/* Added Action column */}
          </tr>
        </thead>
        <tbody>
          {reviews.map((review) => (
            <tr key={review._id}>
              <td>{review.title}</td>
              <td>{review.author}</td>
              <td>{review.rating} / 5</td>
              <td>{review.reviewText}</td>
              <td>
                {/* Action buttons to edit or delete review */}
                <button onClick={() => handleEdit(review._id)}>Edit</button>
                <button onClick={() => handleDelete(review._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ReviewList;
