import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ReviewList() {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/reviews')
      .then(res => setReviews(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="review-list-container">
      <h2 className="title">Book Reviews</h2>
      <table className="reviews-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Rating</th>
            <th>Review</th>
          </tr>
        </thead>
        <tbody>
          {reviews.map((review) => (
            <tr key={review._id}>
              <td>{review.title}</td>
              <td>{review.author}</td>
              <td>{review.rating} / 5</td>
              <td>{review.reviewText}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ReviewList;

// CSS (included in the same file)
const styles = {
  reviewListContainer: {
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    textAlign: 'center',
    fontSize: '2em',
    marginBottom: '20px',
  },
  reviewsTable: {
    width: '100%',
    borderCollapse: 'collapse',
    margin: '0 auto',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  tableHeader: {
    backgroundColor: '#f4f4f4',
    textAlign: 'left',
    padding: '12px 15px',
    fontSize: '1.1em',
    color: '#333',
  },
  tableRow: {
    backgroundColor: '#fff',
    borderBottom: '1px solid #ddd',
    textAlign: 'left',
    padding: '12px 15px',
  },
  tableRowHover: {
    backgroundColor: '#f9f9f9',
  },
  tableData: {
    fontSize: '1em',
    color: '#555',
  },
};

// Apply styles
const styleSheet = document.styleSheets[0];
const injectStyles = () => {
  const style = document.createElement('style');
  style.innerHTML = `
    .review-list-container {
      padding: 20px;
      font-family: Arial, sans-serif;
    }
    .title {
      text-align: center;
      font-size: 2em;
      margin-bottom: 20px;
    }
    .reviews-table {
      width: 100%;
      border-collapse: collapse;
      margin: 0 auto;
      border-radius: 8px;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    }
    .reviews-table th {
      background-color: #f4f4f4;
      text-align: left;
      padding: 12px 15px;
      font-size: 1.1em;
      color: #333;
    }
    .reviews-table td {
      background-color: #fff;
      border-bottom: 1px solid #ddd;
      text-align: left;
      padding: 12px 15px;
      font-size: 1em;
      color: #555;
    }
    .reviews-table tr:hover {
      background-color: #f9f9f9;
    }
  `;
  styleSheet.insertBefore(style, styleSheet.firstChild);
};

injectStyles();
