import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ReviewList() {
  const [reviews, setReviews] = useState([]);
  
  // Fetch reviews on component mount
  useEffect(() => {
    axios.get('http://localhost:5000/reviews')
      .then(res => {
        console.log('Reviews data:', res.data);
        setReviews(res.data);
      })
      .catch(err => {
        console.error('Error fetching reviews:', err);
      });
  }, []);
  
  // Function to delete a review
  const deleteReview = (id) => {
    axios.delete(`http://localhost:5000/reviews/${id}`)
      .then(res => {
        console.log('Review deleted:', res);
        setReviews(reviews.filter(review => review._id !== id)); // Remove deleted review from state
      })
      .catch(err => {
        console.error('Error deleting review:', err);
      });
  };

  // Function to add a new review (you can add a form or modal to handle this)
  const addReview = (newReview) => {
    axios.post('http://localhost:5000/reviews', newReview)
      .then(res => {
        console.log('Review added:', res);
        setReviews([...reviews, res.data]); // Add the new review to state
      })
      .catch(err => {
        console.error('Error adding review:', err);
      });
  };

  return (
    <div className="review-list-container">
      <h2 className="title">Book Reviews</h2>
      
      {/* Add a button to add new review */}
      <button onClick={() => addReview({
        title: 'New Book',
        author: 'New Author',
        rating: 5,
        reviewText: 'Great book!'
      })}>Add Review</button>

      <table className="reviews-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Rating</th>
            <th>Review</th>
            <th>Actions</th> {/* New column for actions */}
          </tr>
        </thead>
        <tbody>
          {reviews.map((review) => (
            <tr key={review._id}>
              <td>{review.title}</td>
              <td>{review.author}</td>
              <td>{review.rating} / 5</td>
              <td>{review.reviewText}</td>
              <td>
                {/* Add Delete button with corresponding function */}
                <button onClick={() => deleteReview(review._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ReviewList;

// Inject styles using a <style> tag
const style = document.createElement('style');
style.innerHTML = `
  .review-list-container {
    padding: 20px;
    font-family: Arial, sans-serif;
  }
  .title {
    text-align: center;
    font-size: 2em;
    margin-bottom: 20px;
  }
  .reviews-table {
    width: 100%;
    border-collapse: collapse;
    margin: 0 auto;
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }
  .reviews-table th {
    background-color: #f4f4f4;
    text-align: left;
    padding: 12px 15px;
    font-size: 1.1em;
    color: #333;
  }
  .reviews-table td {
    background-color: #fff;
    border-bottom: 1px solid #ddd;
    text-align: left;
    padding: 12px 15px;
    font-size: 1em;
    color: #555;
  }
  .reviews-table tr:hover {
    background-color: #f9f9f9;
  }
  .reviews-table button {
    padding: 5px 10px;
    background-color: red;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
  }
  .reviews-table button:hover {
    background-color: darkred;
  }
`;

document.head.appendChild(style);
