import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ReviewList.css'; // Importing the external CSS file

function ReviewList() {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/reviews')
      .then(res => {
        console.log('Reviews data:', res.data); // Check data in console
        setReviews(res.data); // Set reviews data
        setLoading(false);  // Set loading to false after data is fetched
      })
      .catch(err => {
        console.error('Error fetching reviews:', err);
        setLoading(false); // Set loading to false in case of error
      });
  }, []);

  return (
    <div className="review-list-container">
      <h2 className="title">Book Reviews</h2>
      {loading ? (
        <p>Loading...</p> // Show loading text until data is fetched
      ) : (
        <table className="reviews-table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Author</th>
              <th>Rating</th>
              <th>Review</th>
            </tr>
          </thead>
          <tbody>
            {reviews.map((review) => (
              <tr key={review._id}>
                <td>{review.title}</td>
                <td>{review.author}</td>
                <td>{review.rating} / 5</td>
                <td>{review.reviewText}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ReviewList;

// Inject styles using a <style> tag
const style = document.createElement('style');
style.innerHTML = `
  .review-list-container {
    padding: 20px;
    font-family: Arial, sans-serif;
  }
  .title {
    text-align: center;
    font-size: 2em;
    margin-bottom: 20px;
  }
  .reviews-table {
    width: 100%;
    border-collapse: collapse;
    margin: 0 auto;
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  }
  .reviews-table th {
    background-color: #f4f4f4;
    text-align: left;
    padding: 12px 15px;
    font-size: 1.1em;
    color: #333;
  }
  .reviews-table td {
    background-color: #fff;
    border-bottom: 1px solid #ddd;
    text-align: left;
    padding: 12px 15px;
    font-size: 1em;
    color: #555;
  }
  .reviews-table tr:hover {
    background-color: #f9f9f9;
  }
`;
document.head.appendChild(style);
