import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function EditReview() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    rating: 1,
    reviewText: '',
  });

  useEffect(() => {
    axios.get(`http://localhost:5000/reviews/${id}`)
      .then(res => setFormData(res.data))
      .catch(err => console.error(err));
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/reviews/${id}`, formData)
      .then(() => {
        alert('Review Updated');
        navigate('/');
      })
      .catch(err => console.error(err));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={formData.title} onChange={e => setFormData({ ...formData, title: e.target.value })} />
      <input type="text" value={formData.author} onChange={e => setFormData({ ...formData, author: e.target.value })} />
      <input type="number" value={formData.rating} min="1" max="5" onChange={e => setFormData({ ...formData, rating: e.target.value })} />
      <textarea value={formData.reviewText} onChange={e => setFormData({ ...formData, reviewText: e.target.value })}></textarea>
      <button type="submit">Update Review</button>
    </form>
  );
}

export default EditReview;
