import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EditReview.css';  // Make sure to import the CSS file


function EditReview() {
  const { id } = useParams(); // Get the ID from the URL
  const [review, setReview] = useState(null);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [rating, setRating] = useState('');
  const [reviewText, setReviewText] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/reviews/${id}`)
      .then(res => {
        setReview(res.data);
        setTitle(res.data.title);
        setAuthor(res.data.author);
        setRating(res.data.rating);
        setReviewText(res.data.reviewText);
      })
      .catch(err => {
        console.error('Error fetching review:', err);
      });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedReview = {
      title,
      author,
      rating,
      reviewText
    };

    axios.put(`http://localhost:5000/reviews/${id}`, updatedReview)
      .then(res => {
        console.log('Review updated:', res.data);
        navigate('/'); // Redirect to the reviews list page after update
      })
      .catch(err => {
        console.error('Error updating review:', err);
      });
  };

  if (!review) return <div>Loading...</div>;

  return (
    <div className="edit-review-container">
      <h2>Edit Review</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input 
            type="text" 
            value={title} 
            onChange={(e) => setTitle(e.target.value)} 
          />
        </div>
        <div>
          <label>Author</label>
          <input 
            type="text" 
            value={author} 
            onChange={(e) => setAuthor(e.target.value)} 
          />
        </div>
        <div>
          <label>Rating</label>
          <input 
            type="number" 
            value={rating} 
            onChange={(e) => setRating(e.target.value)} 
            max="5" 
            min="1" 
          />
        </div>
        <div>
          <label>Review</label>
          <textarea 
            value={reviewText} 
            onChange={(e) => setReviewText(e.target.value)} 
          />
        </div>
        <button type="submit">Update Review</button>
      </form>
    </div>
  );
}

export default EditReview;
